from gurobipy import *glass_co = Model ("glass_co")x  = glass_co.addVar (vtype = GRB.INTEGER, name = "door_batch")y = glass_co.addVar (vtype  = GRB.INTEGER, name = "window_batch")glass_co.addConstr (1 * x <= 4, name = "constraint_1")glass_co.addConstr (2 * y <= 12, name ="constraint_2")glass_co.addConstr (3 * x + 2* y <= 18, name = "constraint_3")Z = 3000 * x + 5000*yglass_co.setObjective (Z, GRB.MAXIMIZE)glass_co.optimize()if glass_co.status == GRB.OPTIMAL:    print ("optimal value is: ", glass_co.objVal)glass_co.printAttr('X')print ("Batch of doors: ", x.X)    print ("Batch of windows: ", y.X)